package WrapperClass;

public class AutoBoxing {

	public static void main(String[] args) {
	 	// TODO Auto-generated method stub
      int a=10;
      Integer b=a;
      System.out.println(b);
      
      double d=22.2;
      Double b1=new Double(d);
      System.out.println(b1);
      
      char ch='A';
      Character c=ch;
      System.out.println(c);
	}

}
